GreenButtonDataPage_js = function(runBeforeShow) { /* Object & array with components "name-to-id" mapping */
    var n2id_buf = {
        'mobilebutton_16': 'GreenButtonDataPage_mobilebutton_16',
        'mobilegrid_2': 'GreenButtonDataPage_mobilegrid_2',
        'mobilegridcell_3': 'GreenButtonDataPage_mobilegridcell_3',
        'mobilelabel_7': 'GreenButtonDataPage_mobilelabel_7',
        'mobilegridcell_4': 'GreenButtonDataPage_mobilegridcell_4',
        'mobilelabel_8': 'GreenButtonDataPage_mobilelabel_8',
        'mobilegridcell_5': 'GreenButtonDataPage_mobilegridcell_5',
        'mobilegridcell_6': 'GreenButtonDataPage_mobilegridcell_6',
        'mobilegrid_11': 'GreenButtonDataPage_mobilegrid_11',
        'mobilegridcell_12': 'GreenButtonDataPage_mobilegridcell_12',
        'IntervalInputlabel': 'GreenButtonDataPage_IntervalInputlabel',
        'mobilegridcell_13': 'GreenButtonDataPage_mobilegridcell_13',
        'Unitslabel': 'GreenButtonDataPage_Unitslabel',
        'mobilegridcell_14': 'GreenButtonDataPage_mobilegridcell_14',
        'mobilegridcell_15': 'GreenButtonDataPage_mobilegridcell_15'
    };

    if ("n2id" in window && window.n2id !== undefined) {
        $.extend(n2id, n2id_buf);
    } else {
        window.n2id = n2id_buf;
    }

    if (navigator.userAgent.indexOf("IEMobile") != -1) {
        //Fixing issue https://github.com/jquery/jquery-mobile/issues/5424 on Windows Phone
        $("div[data-role=page] div[data-role=footer]").css("bottom", "-36px");
    }

    if (Appery.getTargetPlatform == "I") {
        $.each(window.n2id, function(name, id) {
            var elApperyRole = $("#" + id).attr("data-role");
            if (elApperyRole != "appery_label" && elApperyRole != "appery_link") {
                var appleDataSelector = "#" + id + "[x-apple-data-detectors], #" + id + " [x-apple-data-detectors]";
                $(appleDataSelector).attr("x-apple-data-detectors", "false");
            }
        });
    }

    Appery.CurrentScreen = 'GreenButtonDataPage';

    /*
     * Nonvisual components
     */
    var datasources = [];

    greenQueryService = new Appery.DataSource(Day1_intervalReading_query_service, {
        'onComplete': function(jqXHR, textStatus) {

            $t.refreshScreenFormElements("GreenButtonDataPage");
        },
        'onSuccess': function(data) {},
        'onError': function(jqXHR, textStatus, errorThrown) {},
        'responseMapping': [{
            'PATH': ['$'],
            'ID': 'mobilegrid_11',
            'SET': [{
                'PATH': ['IntervalReading_Id'],
                'ID': 'IntervalInputlabel',
                'ATTR': '@'
            }, {
                'PATH': ['value'],
                'ID': 'Unitslabel',
                'ATTR': '@'
            }]
        }],
        'requestMapping': [{
            'PATH': ['X-Appery-Database-Id'],
            'TYPE': 'STRING',
            'HEADER': true,
            'ATTR': '{database_id}'
        }]
    });

    datasources.push(greenQueryService);

    /*
     * Events and handlers
     */

    // Before Show
    GreenButtonDataPage_beforeshow = function() {
        Appery.CurrentScreen = "GreenButtonDataPage";
        for (var idx = 0; idx < datasources.length; idx++) {
            datasources[idx].__setupDisplay();
        }
    }

    // On Load
    screen_B2BE_onLoad = GreenButtonDataPage_onLoad = function() {
        screen_B2BE_elementsExtraJS();
        var imageUrl = Appery.getImagePath('white.png');
        $('div[data-role="page"]').css('background-image', 'url(' + imageUrl + ')');
        try {
            greenQueryService.execute({})
        } catch (ex) {
            console.log(ex.name + '  ' + ex.message);
            hideSpinner();
        };
        var imageUrl = Appery.getImagePath('white.png');
        $('div[data-role="page"]').css('background-image', 'url(' + imageUrl + ')');

        // TODO fire device events only if necessary (with JS logic)
        GreenButtonDataPage_deviceEvents();
        GreenButtonDataPage_windowEvents();
        screen_B2BE_elementsEvents();
    }

    // screen window events
    screen_B2BE_windowEvents = GreenButtonDataPage_windowEvents = function() {

        $('#GreenButtonDataPage').bind('pageshow orientationchange', function() {
            var _page = this;
            adjustContentHeightWithPadding(_page);
        });

    }

    // device events
    GreenButtonDataPage_deviceEvents = function() {

        document.addEventListener("deviceready", function() {

        });
    }

    // screen elements extra js
    screen_B2BE_elementsExtraJS = GreenButtonDataPage_elementsExtraJS = function() {
        // screen (GreenButtonDataPage) extra code

    }

    // screen elements handler
    screen_B2BE_elementsEvents = GreenButtonDataPage_elementsEvents = function() {

        $("a :input,a a,a fieldset label").live({
            click: function(event) {
                event.stopPropagation();
            }
        });

        $('#GreenButtonDataPage_mobileheader [name="mobilebutton_16"]').die().live({
            click: function() {
                if (!$(this).attr('disabled')) {
                    Appery.navigateTo('FirstScreen', {
                        transition: 'fade',
                        reverse: false
                    });

                }
            },
        });

    }

    $("#GreenButtonDataPage").die("pagebeforeshow").live("pagebeforeshow", function(event, ui) {
        GreenButtonDataPage_beforeshow();
    });

    if (runBeforeShow) {
        GreenButtonDataPage_beforeshow();
    } else {
        GreenButtonDataPage_onLoad();
    }

}

$("#GreenButtonDataPage").die("pageinit").live("pageinit", function(event, ui) {
    Appery.processSelectMenu($(this));
    GreenButtonDataPage_js();
});