RaceEventScreen_js = function(runBeforeShow) { /* Object & array with components "name-to-id" mapping */
    var n2id_buf = {
        'mobilebutton_1': 'RaceEventScreen_mobilebutton_1',
        'mobilebutton_16': 'RaceEventScreen_mobilebutton_16',
        'mobilecollapsblock_3': 'RaceEventScreen_mobilecollapsblock_3',
        'dateblockheader': 'RaceEventScreen_dateblockheader',
        'dateeventabel': 'RaceEventScreen_dateeventabel',
        'mobilecollapsblockcontent_5': 'RaceEventScreen_mobilecollapsblockcontent_5',
        'mobilegrid_9': 'RaceEventScreen_mobilegrid_9',
        'mobilegridcell_10': 'RaceEventScreen_mobilegridcell_10',
        'mobilelabel_14': 'RaceEventScreen_mobilelabel_14',
        'mobilegridcell_11': 'RaceEventScreen_mobilegridcell_11',
        'cashPrizeInputlabel': 'RaceEventScreen_cashPrizeInputlabel',
        'mobilegridcell_12': 'RaceEventScreen_mobilegridcell_12',
        'mobilelabel_15': 'RaceEventScreen_mobilelabel_15',
        'mobilegridcell_13': 'RaceEventScreen_mobilegridcell_13',
        'targetInputlabel': 'RaceEventScreen_targetInputlabel',
        'joinEventbutton': 'RaceEventScreen_joinEventbutton'
    };

    if ("n2id" in window && window.n2id !== undefined) {
        $.extend(n2id, n2id_buf);
    } else {
        window.n2id = n2id_buf;
    }

    if (navigator.userAgent.indexOf("IEMobile") != -1) {
        //Fixing issue https://github.com/jquery/jquery-mobile/issues/5424 on Windows Phone
        $("div[data-role=page] div[data-role=footer]").css("bottom", "-36px");
    }

    if (Appery.getTargetPlatform == "I") {
        $.each(window.n2id, function(name, id) {
            var elApperyRole = $("#" + id).attr("data-role");
            if (elApperyRole != "appery_label" && elApperyRole != "appery_link") {
                var appleDataSelector = "#" + id + "[x-apple-data-detectors], #" + id + " [x-apple-data-detectors]";
                $(appleDataSelector).attr("x-apple-data-detectors", "false");
            }
        });
    }

    Appery.CurrentScreen = 'RaceEventScreen';

    /*
     * Nonvisual components
     */
    var datasources = [];

    eventCreate = new Appery.DataSource(SmartRace_UserEvent_create_service, {
        'onComplete': function(jqXHR, textStatus) {

            $t.refreshScreenFormElements("RaceEventScreen");
        },
        'onSuccess': function(data) {},
        'onError': function(jqXHR, textStatus, errorThrown) {},
        'responseMapping': [],
        'requestMapping': [{
            'PATH': ['X-Appery-Database-Id'],
            'TYPE': 'STRING',
            'HEADER': true,
            'ATTR': '{database_id}'
        }, {
            'PATH': ['EventDate'],
            'TYPE': 'STRING',
            'ID': '___local_storage___',
            'ATTR': 'eventdate'
        }, {
            'PATH': ['Target'],
            'TYPE': 'STRING',
            'ID': '___local_storage___',
            'ATTR': 'target'
        }, {
            'PATH': ['CashPrize'],
            'TYPE': 'STRING',
            'ID': '___local_storage___',
            'ATTR': 'prize'
        }, {
            'PATH': ['X-Appery-Database-Id'],
            'TYPE': 'STRING',
            'HEADER': true,
            'ATTR': '{database_id}'
        }]
    });

    datasources.push(eventCreate);

    eventQuery = new Appery.DataSource(SmartRace_events_query_service, {
        'onComplete': function(jqXHR, textStatus) {

            $t.refreshScreenFormElements("RaceEventScreen");
        },
        'onSuccess': function(data) {},
        'onError': function(jqXHR, textStatus, errorThrown) {},
        'responseMapping': [{
            'PATH': ['$'],
            'ID': 'mobilecollapsblock_3',
            'SET': [{
                'PATH': ['EventDate'],
                'ID': 'dateeventabel',
                'ATTR': '@'
            }, {
                'PATH': ['CashPrize'],
                'ID': 'cashPrizeInputlabel',
                'ATTR': '@'
            }, {
                'PATH': ['Target'],
                'ID': 'targetInputlabel',
                'ATTR': '@'
            }]
        }],
        'requestMapping': [{
            'PATH': ['X-Appery-Database-Id'],
            'TYPE': 'STRING',
            'HEADER': true,
            'ATTR': '{database_id}'
        }]
    });

    datasources.push(eventQuery);

    /*
     * Events and handlers
     */

    // Before Show
    RaceEventScreen_beforeshow = function() {
        Appery.CurrentScreen = "RaceEventScreen";
        for (var idx = 0; idx < datasources.length; idx++) {
            datasources[idx].__setupDisplay();
        }
    }

    // On Load
    screen_E55F_onLoad = RaceEventScreen_onLoad = function() {
        screen_E55F_elementsExtraJS();
        var imageUrl = Appery.getImagePath('white.png');
        $('div[data-role="page"]').css('background-image', 'url(' + imageUrl + ')');
        try {
            eventQuery.execute({})
        } catch (ex) {
            console.log(ex.name + '  ' + ex.message);
            hideSpinner();
        };

        // TODO fire device events only if necessary (with JS logic)
        RaceEventScreen_deviceEvents();
        RaceEventScreen_windowEvents();
        screen_E55F_elementsEvents();
    }

    // screen window events
    screen_E55F_windowEvents = RaceEventScreen_windowEvents = function() {

        $('#RaceEventScreen').bind('pageshow orientationchange', function() {
            var _page = this;
            adjustContentHeightWithPadding(_page);
        });

    }

    // device events
    RaceEventScreen_deviceEvents = function() {

        document.addEventListener("deviceready", function() {

        });
    }

    // screen elements extra js
    screen_E55F_elementsExtraJS = RaceEventScreen_elementsExtraJS = function() {
        // screen (RaceEventScreen) extra code

        /* mobilecollapsblock_3 */

        $("#RaceEventScreen_mobilecollapsblock_3 .ui-collapsible-heading-toggle").attr("tabindex", "3");

    }

    // screen elements handler
    screen_E55F_elementsEvents = RaceEventScreen_elementsEvents = function() {

        $("a :input,a a,a fieldset label").live({
            click: function(event) {
                event.stopPropagation();
            }
        });

        $('#RaceEventScreen_mobileheader [name="mobilebutton_16"]').die().live({
            click: function() {
                if (!$(this).attr('disabled')) {
                    Appery.navigateTo('FirstScreen', {
                        transition: 'fade',
                        reverse: false
                    });

                }
            },
        });

        $('#RaceEventScreen_mobilecontainer [name="joinEventbutton"]').die().live({
            click: function() {
                if (!$(this).attr('disabled')) {
                    setVar_('target', 'RaceEventScreen_targetInputlabel', 'text', '', this);
                    setVar_('prize', 'RaceEventScreen_cashPrizeInputlabel', 'text', '', this);
                    setVar_('eventdate', 'RaceEventScreen_dateeventabel', 'text', '', this);
                    Appery.navigateTo('EventJoinSuccess', {
                        reverse: false
                    });
                    try {
                        eventCreate.execute({})
                    } catch (ex) {
                        console.log(ex.name + '  ' + ex.message);
                        hideSpinner();
                    };

                }
            },
        });

    }

    $("#RaceEventScreen").die("pagebeforeshow").live("pagebeforeshow", function(event, ui) {
        RaceEventScreen_beforeshow();
    });

    if (runBeforeShow) {
        RaceEventScreen_beforeshow();
    } else {
        RaceEventScreen_onLoad();
    }

}

