ScoreBoard_js = function(runBeforeShow) { /* Object & array with components "name-to-id" mapping */
    var n2id_buf = {
        'buttonback': 'ScoreBoard_buttonback',
        'mobilegrid_1': 'ScoreBoard_mobilegrid_1',
        'mobilegridcell_2': 'ScoreBoard_mobilegridcell_2',
        'mobilelabel_8': 'ScoreBoard_mobilelabel_8',
        'mobilegridcell_3': 'ScoreBoard_mobilegridcell_3',
        'mobilelabel_9': 'ScoreBoard_mobilelabel_9',
        'mobilegridcell_6': 'ScoreBoard_mobilegridcell_6',
        'mobilelabel_10': 'ScoreBoard_mobilelabel_10',
        'mobilegridcell_4': 'ScoreBoard_mobilegridcell_4',
        'mobilegridcell_5': 'ScoreBoard_mobilegridcell_5',
        'mobilegridcell_7': 'ScoreBoard_mobilegridcell_7',
        'mobilegrid_11': 'ScoreBoard_mobilegrid_11',
        'mobilegridcell_12': 'ScoreBoard_mobilegridcell_12',
        'Numberlabel': 'ScoreBoard_Numberlabel',
        'mobilegridcell_13': 'ScoreBoard_mobilegridcell_13',
        'Racelabel': 'ScoreBoard_Racelabel',
        'mobilegridcell_16': 'ScoreBoard_mobilegridcell_16',
        'Savinglabel': 'ScoreBoard_Savinglabel',
        'mobilegridcell_14': 'ScoreBoard_mobilegridcell_14',
        'mobilegridcell_15': 'ScoreBoard_mobilegridcell_15',
        'mobilegridcell_17': 'ScoreBoard_mobilegridcell_17'
    };

    if ("n2id" in window && window.n2id !== undefined) {
        $.extend(n2id, n2id_buf);
    } else {
        window.n2id = n2id_buf;
    }

    if (navigator.userAgent.indexOf("IEMobile") != -1) {
        //Fixing issue https://github.com/jquery/jquery-mobile/issues/5424 on Windows Phone
        $("div[data-role=page] div[data-role=footer]").css("bottom", "-36px");
    }

    if (Appery.getTargetPlatform == "I") {
        $.each(window.n2id, function(name, id) {
            var elApperyRole = $("#" + id).attr("data-role");
            if (elApperyRole != "appery_label" && elApperyRole != "appery_link") {
                var appleDataSelector = "#" + id + "[x-apple-data-detectors], #" + id + " [x-apple-data-detectors]";
                $(appleDataSelector).attr("x-apple-data-detectors", "false");
            }
        });
    }

    Appery.CurrentScreen = 'ScoreBoard';

    /*
     * Nonvisual components
     */
    var datasources = [];

    scoreQueryService = new Appery.DataSource(SmartRace_scoreboard_query_service, {
        'onComplete': function(jqXHR, textStatus) {

            $t.refreshScreenFormElements("ScoreBoard");
        },
        'onSuccess': function(data) {},
        'onError': function(jqXHR, textStatus, errorThrown) {},
        'responseMapping': [{
            'PATH': ['$'],
            'ID': 'mobilegrid_11',
            'SET': [{
                'PATH': ['name'],
                'ID': 'Numberlabel',
                'ATTR': '@'
            }, {
                'PATH': ['races'],
                'ID': 'Racelabel',
                'ATTR': '@'
            }, {
                'PATH': ['saving'],
                'ID': 'Savinglabel',
                'ATTR': '@'
            }]
        }],
        'requestMapping': [{
            'PATH': ['X-Appery-Database-Id'],
            'TYPE': 'STRING',
            'HEADER': true,
            'ATTR': '{database_id}'
        }]
    });

    datasources.push(scoreQueryService);

    /*
     * Events and handlers
     */

    // Before Show
    ScoreBoard_beforeshow = function() {
        Appery.CurrentScreen = "ScoreBoard";
        for (var idx = 0; idx < datasources.length; idx++) {
            datasources[idx].__setupDisplay();
        }
    }

    // On Load
    screen_EEFF_onLoad = ScoreBoard_onLoad = function() {
        screen_EEFF_elementsExtraJS();
        try {
            scoreQueryService.execute({})
        } catch (ex) {
            console.log(ex.name + '  ' + ex.message);
            hideSpinner();
        };

        // TODO fire device events only if necessary (with JS logic)
        ScoreBoard_deviceEvents();
        ScoreBoard_windowEvents();
        screen_EEFF_elementsEvents();
    }

    // screen window events
    screen_EEFF_windowEvents = ScoreBoard_windowEvents = function() {

        $('#ScoreBoard').bind('pageshow orientationchange', function() {
            var _page = this;
            adjustContentHeightWithPadding(_page);
        });

    }

    // device events
    ScoreBoard_deviceEvents = function() {

        document.addEventListener("deviceready", function() {

        });
    }

    // screen elements extra js
    screen_EEFF_elementsExtraJS = ScoreBoard_elementsExtraJS = function() {
        // screen (ScoreBoard) extra code

    }

    // screen elements handler
    screen_EEFF_elementsEvents = ScoreBoard_elementsEvents = function() {

        $("a :input,a a,a fieldset label").live({
            click: function(event) {
                event.stopPropagation();
            }
        });

        $('#ScoreBoard_mobileheader [name="buttonback"]').die().live({
            click: function() {
                if (!$(this).attr('disabled')) {
                    Appery.navigateTo('FirstScreen', {
                        reverse: false
                    });

                }
            },
        });

    }

    $("#ScoreBoard").die("pagebeforeshow").live("pagebeforeshow", function(event, ui) {
        ScoreBoard_beforeshow();
    });

    if (runBeforeShow) {
        ScoreBoard_beforeshow();
    } else {
        ScoreBoard_onLoad();
    }

}

$("#ScoreBoard").die("pageinit").live("pageinit", function(event, ui) {
    Appery.processSelectMenu($(this));
    ScoreBoard_js();
});